interface FMResult {
  distance: number
  value: string | null
}

interface FuzzyMatching {
  get: (testStr: string) => FMResult
}

interface FMConstructor {
  new (strList: string[]) : FuzzyMatching
}

declare const FuzzyMatching: FMConstructor

declare module 'fuzzy-matching' {
  export = FuzzyMatching
}